script:
let today = frappe.datetime.get_today();
let yesterday = frappe.datetime.add_days(today, -1);

// Get company currency
let company_currency = frappe.defaults.get_default("currency");

// -------- TODAY PAID SALES --------
frappe.call({
  method: "frappe.client.get_list",
  args: {
    doctype: "Sales Invoice",
    filters: {
      status: "Paid",
      posting_date: today
    },
    fields: ["grand_total"],
    limit_page_length: 0
  },
  callback: function (r) {
    let total = 0;
    (r.message || []).forEach(row => {
      total += row.grand_total || 0;
    });

    root_element.querySelector("#today-sales").textContent =
      format_currency(total, company_currency);
  }
});

// Click ‚Üí Today Paid Sales
root_element.querySelector("#today-sales-box").addEventListener("click", function () {
  frappe.set_route("List", "Sales Invoice", {
    status: "Paid",
    posting_date: today
  });
});


// -------- YESTERDAY PAID SALES --------
frappe.call({
  method: "frappe.client.get_list",
  args: {
    doctype: "Sales Invoice",
    filters: {
      status: "Paid",
      posting_date: yesterday
    },
    fields: ["grand_total"],
    limit_page_length: 0
  },
  callback: function (r) {
    let total = 0;
    (r.message || []).forEach(row => {
      total += row.grand_total || 0;
    });

    root_element.querySelector("#yesterday-sales").textContent =
      format_currency(total, company_currency);
  }
});

// Click ‚Üí Yesterday Paid Sales
root_element.querySelector("#yesterday-sales-box").addEventListener("click", function () {
  frappe.set_route("List", "Sales Invoice", {
    status: "Paid",
    posting_date: yesterday
  });
});

